import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, TextField, Typography } from '@mui/material';

const OrderManagement = () => {
    const [orders, setOrders] = useState([]);
    const [status, setStatus] = useState('');
    const [orderId, setOrderId] = useState('');

    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/v1/orders');
            setOrders(response.data);
        } catch (error) {
            console.error('Error fetching orders:', error);
        }
    };

    const updateOrderStatus = async () => {
        try {
            await axios.patch(`http://localhost:3001/api/v1/orders/${orderId}`, { status });
            fetchOrders(); // Refresh orders after update
        } catch (error) {
            console.error('Error updating order status:', error);
        }
    };

    return (
        <div>
            <Typography variant="h5">Order Management</Typography>
            <div>
                <TextField
                    label="Order ID"
                    value={orderId}
                    onChange={(e) => setOrderId(e.target.value)}
                />
                <TextField
                    label="New Status"
                    value={status}
                    onChange={(e) => setStatus(e.target.value)}
                />
                <Button variant="contained" color="primary" onClick={updateOrderStatus}>
                    Update Status
                </Button>
            </div>
            <div>
                <Typography variant="h6">Orders</Typography>
                <ul>
                    {orders.map((order) => (
                        <li key={order._id}>
                            Order ID: {order._id} | Status: {order.status}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default OrderManagement;
