import React, { useState } from 'react';
import axios from 'axios';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const defaultTheme = createTheme();

export default function MenuItemForm() {
    const [itemData, setItemData] = useState({
        orderId: '',
        userId: '',
        items: [{ menuItemId: '', quantity: '' }],
        totalAmount: '',
        orderStatus: 'pending',
        orderTime: new Date().toISOString(),
        tableNumber: ''
    });
    const [formError, setFormError] = useState('');

    const handleChange = (event) => {
        const { name, value } = event.target;
        setItemData({ ...itemData, [name]: value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            // Simple client-side validation
            if (itemData.totalAmount <= 0 || itemData.items[0].quantity <= 0) {
                setFormError('Total amount and quantity must be positive numbers.');
                return;
            }

            console.log(itemData);
            await axios.post('http://localhost:3001/api/v1/orders/order', itemData);
            // Add success handling here (e.g., show a success message)
        } catch (error) {
            console.error('Error submitting form:', error);
            setFormError('Error submitting form. Please try again later.');
        }
    };

    return (
        <>
            <ThemeProvider theme={defaultTheme}>
                <Container component="main" maxWidth="xs">
                    <CssBaseline />
                    <Box
                        sx={{
                            marginTop: 8,
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'center',
                        }}
                    >
                        <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
                            <LockOutlinedIcon />
                        </Avatar>
                        <Typography component="h1" variant="h5">
                            Menu Item Form
                        </Typography>
                        <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
                            <Grid container spacing={2}>
                                {/* Add other input fields */}
                                {/* Error message display */}
                                {formError && (
                                    <Grid item xs={12}>
                                        <Typography variant="body1" color="error">
                                            {formError}
                                        </Typography>
                                    </Grid>
                                )}
                            </Grid>
                            <Button type="submit" fullWidth variant="contained" sx={{ mt: 3, mb: 2 }}>
                                Add Order
                            </Button>
                            <Grid container justifyContent="flex-end">
                                <Grid item>
                                    <Link href="#" variant="body2">
                                        Already have an account? Sign in
                                    </Link>
                                </Grid>
                            </Grid>
                        </Box>
                    </Box>
                </Container>
            </ThemeProvider>
        </>
    );
}
