import React, { useState, useEffect } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import axios from 'axios';

const MenuDisplay = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    // Fetch menu items from your backend API
    axios.get('http://localhost:3001/api/v1/menuitems/menu-items')
      .then(response => {
        setMenuItems(response.data);
      })
      .catch(error => {
        console.error('Error fetching menu items:', error);
      });
  }, []);

  return (
    <div>
      {menuItems.map(menuItem => (
        <ActionAreaCard
          key={menuItem._id}
          name={menuItem.name}
          price={menuItem.price}
          description={menuItem.description}
          image={menuItem.image.toString()} // Ensure image is passed as a string
        />
      ))}
    </div>
  );
};

const ActionAreaCard = ({ name, description, image ,price}) => {
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardActionArea>
        <CardMedia
          component="img"
          height="140"
          image={i}
          alt="Image"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {name}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {description}
          </Typography>
          <Typography gutterBottom variant="h5" component="div">
            Rs. {price}.00
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>
  );
};

export default MenuDisplay;
