import React, { useState } from 'react';
import axios from 'axios';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { Container, Typography, Grid, MenuItem, Select, FormControl, InputLabel, Snackbar } from '@mui/material';

const MenuItemForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        price: '',
        category: 'appetizer',
        availability: 'availability',
        image: null,
    });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        setFormData({ ...formData, image: file });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const formDataToSend = new FormData();
            formDataToSend.append('name', formData.name);
            formDataToSend.append('description', formData.description);
            formDataToSend.append('price', formData.price);
            formDataToSend.append('category', formData.category);
            formDataToSend.append('availability', formData.availability);
            formDataToSend.append('image', formData.image);

            await axios.post('http://localhost:3001/api/v1/menuitems/menu-items', formDataToSend, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            setSuccess('Menu item added successfully');
            setFormData({
                name: '',
                description: '',
                price: '',
                category: 'appetizer',
                availability: 'availability',
                image: null,
            });
        } catch (error) {
            setError('Error adding menu item');
            console.error('Error submitting form:', error);
        }
    };

    return (
        <Container maxWidth="sm">
            <Typography variant="h4" align="center" gutterBottom>
                Add Menu Item
            </Typography>
            <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    {/* Form fields */}
                </Grid>
                <Button type="submit" variant="contained" color="primary">
                    Add Item
                </Button>
            </form>
            <Snackbar
                open={!!error || !!success}
                autoHideDuration={6000}
                onClose={() => {
                    setError('');
                    setSuccess('');
                }}
                message={error || success}
            />
        </Container>
    );
};

export default MenuItemForm;
