import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Button from '@mui/material/Button';

const BookTablePage = () => {
    const [availableSeats, setAvailableSeats] = useState(0);
    const [tables, setTables] = useState([]);
    const [seatsAvailableMessage, setSeatsAvailableMessage] = useState('');

    useEffect(() => {
        // Fetch available seats from your backend API
        const fetchAvailableSeats = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/v1/tables/tables');
                setTables(response.data); // Assuming the response data is an array of tables
                const availableTables = response.data.filter(table => table.status === 'available');
                setAvailableSeats(availableTables.length);
                setSeatsAvailableMessage(availableTables.length > 0 ? '' : 'No seats available');
            } catch (error) {
                console.error('Error fetching available seats:', error);
            }
        };

        fetchAvailableSeats();
    }, []);

    const handleBookSeat = async (tableId) => {
        try {
            // Send a request to book the selected seat
            await axios.patch(`http://localhost:3001/api/v1/tables/tables/${tableId}`, { status: 'reserved' });
            // Update availableSeats locally
            setAvailableSeats(availableSeats => availableSeats - 1);
            setSeatsAvailableMessage(availableSeats > 1 ? '' : 'No seats available');
        } catch (error) {
            console.error('Error booking seat:', error);
        }
    };

    return (
        <div>
            <h1>Available Seats: {availableSeats}</h1>
            {seatsAvailableMessage && <p>{seatsAvailableMessage}</p>}
            <div>
                {/* Create a "Book" button for each available seat */}
                {tables.map(table => (
                    table.status === 'available' && (
                        <Button
                            key={table._id}
                            variant="contained"
                            onClick={() => handleBookSeat(table._id)}
                        >
                            Book Table {table.tableNumber}
                        </Button>
                    )
                ))}
            </div>
        </div>
    );
};

export default BookTablePage;
