import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Table from '../../../../server/routes/table_shop_routes';
import Button from '@mui/material/Button';
// import Button from '@mui/material/Button';
// import Stack from '@mui/material/Stack';
// import Box from '@mui/material/Box';

const BookTablePage = () => {
    const [availableSeats, setAvailableSeats] = useState(0);

    useEffect(() => {
        // Fetch available seats from your backend API
        const fetchAvailableSeats = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/v1/tables/tables');
                setAvailableSeats(response.data.availableSeats);
            } catch (error) {
                console.error('Error fetching available seats:', error);
            }
        };

        fetchAvailableSeats();
    }, []);

    const handleBookSeat = async (seatNumber) => {
        try {
            // Send a request to book the selected seat
            await axios.post('http://localhost:3001/api/v1/tables/tables', { seatNumber });
            // Update availableSeats locally or fetch updated data
            // Show a success message or redirect to a confirmation page
        } catch (error) {
            console.error('Error booking seat:', error);
        }
    };

    return (
        <div>
            <h1>Available Seats: {availableSeats}</h1>
            <div>
                {/* Create a "Book" button for each available seat */}
                {[...Array(availableSeats).keys()].map((seatNumber) => (
                    <Button variant="contained" key={seatNumber} onClick={() => handleBookSeat(seatNumber + 1)}>
                        Book Seat {seatNumber + 1}
                    </Button>

                   //<Button variant="contained" href="#contained-buttons">  TAKE AWAY  </Button>
                ))}
            </div>
        </div>
    );
};

export default BookTablePage;
